def cos_sim(vec1, vec2, verbose = False):
    dot_product = np.sum(np.multiply(vec1, vec2))
    denominator_1 = np.sqrt(np.sum(np.square(vec1)))
    denominator_2 = np.sqrt(np.sum(np.square(vec2)))

    if verbose:
        print('numerator {}'.format(dot_product))
        print('denominator 1 {}'.format(denominator_1))
        print('denominator 2 {}'.format(denominator_2))

    return dot_product/(denominator_1 * denominator_2)

